import {
    composeContext,
    elizaLogger,
    type HandlerCallback,
    ModelClass,
    type IAgentRuntime,
    type Memory,
    type State,
    generateObjectDeprecated,
} from "@elizaos/core";
import {
    decodeFunctionData,
    erc20Abi,
    formatUnits,
    parseUnits,
    zeroAddress,
    type Hex,
} from "viem";

import {
    evmWalletProvider,
    initWalletProvider,
    type WalletProvider,
} from "../providers/evmWallet";

import { swapTemplate } from "../templates";
import { QuotePayload, RoutePayload, SupportedChain, SwapParams, SwapResponse } from "../types";
import { PonderAPIProvider } from "../providers/ponder";
import { swapExamplesSingleToken } from "../utils/examples";
export { swapTemplate };

export class SwapAction {
    constructor(private walletProvider: WalletProvider) {}

    async swapSingle(params: SwapParams): Promise<SwapResponse | string> {
        elizaLogger.info("Single swap params same chain:", params);
        const fromAddress = this.walletProvider.getAddress();

        const requiredFields = await this.checkRequiredParams(params);

        if (requiredFields.length > 0) {
            return requiredFields;
        }

        const requiredParams = await this.validateParams(params)

        if (requiredParams.length > 0) {
            return requiredParams;
        }

        const ponderProvider = new PonderAPIProvider();
        const chains = await ponderProvider.getChains();

        const blockExplorer = chains.find((chain) => chain.name.toLowerCase() === params.chain.toLowerCase())?.blockExplorer;

        const inputChainId = String(await ponderProvider.getChainId(params.chain, chains));
        const outputChainId = String(await ponderProvider.getChainId(params.chain, chains));

        const resp: SwapResponse = {
           fromChain: params.chain as SupportedChain,
           toChain: params.chain as SupportedChain,
           fromTokenSymbol: params.inputTokenSymbol,
           toTokenSymbol: params.outputTokenSymbol,
           fromToken: params.inputToken,
           toToken: params.outputToken,
           txHash: blockExplorer + "tx/",
           recipient:fromAddress,
           fromAmount: params.amount,
           toAmount: ""
        }

        const publicClient = this.walletProvider.getPublicClient(
            params.chain
        );

        let decimals;

        if (params.inputToken === zeroAddress) {
            decimals = 18;
        } else {
            decimals = await publicClient.readContract({
                address: params.inputToken as `0x${string}`,
                abi: erc20Abi,
                functionName: "decimals",
            });
        }

        params.amount = parseUnits(params.amount, decimals).toString();

        this.walletProvider.switchChain(params.chain);

        const quotePayload: QuotePayload = {
            fromChain: inputChainId,
            toChain: outputChainId,
            fromToken: [params.inputToken],
            toToken: [params.outputToken],
            fromAmount: [params.amount],
            slippage: 0.5, //TODO: slippage fix now returning "null" in string form
            fromAddress: fromAddress,
            toAddress: fromAddress,
            provider: ""
        }

        const quote = await ponderProvider.getQuote(quotePayload);

        if (!quote) {
            throw new Error("No quote found for the given input");
        }

        const routePayload: RoutePayload = {
            fromChain: inputChainId,
            toChain: outputChainId,
            fromToken: [params.inputToken],
            toToken: [params.outputToken],
            fromAmount: [params.amount],
            slippage: params.slippage,
            fromAddress: fromAddress,
            provider: quote.protocol,
            routeId: quote.routeId
        }

        let route = await ponderProvider.getRoute(routePayload);

        if (!route) {
            throw new Error("No route found for the given quote");
        }

        if (route.approvalTx) {
            const approveAmount = BigInt(params.amount)
            const token = await this.walletProvider.formatAddress(params.inputToken)

            const { args } = decodeFunctionData({
                abi: erc20Abi,
                data: route.approvalTx[0].data
              })

            const spender = await this.walletProvider.formatAddress(args[0] as string)

            const allowance = await this.walletProvider.checkERC20Allowance(
                params.chain,
                token,
                fromAddress,
                spender
            );
            if (allowance < approveAmount) {
                elizaLogger.info(
                    `Increasing ERC20 token ${params.inputTokenSymbol} allowance for ${route.protocol}. ${approveAmount - allowance} more needed`
                );
                const txHash = await this.walletProvider.approveERC20(
                    params.chain,
                    token,
                    spender,
                    approveAmount
                );
                await publicClient.waitForTransactionReceipt({
                    hash: txHash,
                });

                elizaLogger.info(`successfully approved ${params.inputTokenSymbol} for swap`);
            }
        }

        route = await ponderProvider.getRoute(routePayload);

        if (!route.executionTx) {
            throw new Error("No execution data found for the given route");
        }

        const options: { gas?: bigint; gasPrice?: bigint; data?: Hex } = {}

        options.gas = BigInt(route.executionTx.gasLimit);
        options.gasPrice = BigInt(route.executionTx.gasPrice);
        options.data = route.executionTx.data;

        const txHash = await this.walletProvider.swapToken(params.chain, route.executionTx.to, BigInt(route.executionTx.value), options)

        if (!txHash || txHash === "0x") {
            throw new Error("Get transaction hash failed");
        }

        // wait for the transaction to be confirmed
        await publicClient.waitForTransactionReceipt({
            hash: txHash,
        });

        const toAmount = formatUnits(quote.transactionDetails[0].toToken[0].toAmount,  quote.transactionDetails[0].toToken[0].decimals);

        resp.toAmount = toAmount.toString(); 
        resp.txHash = resp.txHash + txHash;

        return resp;

    }

    async checkRequiredParams(params: SwapParams): Promise<string> {

        let requiredFieldsText = "";

        if (!params.inputTokenSymbol) {
            requiredFieldsText += `- inputTokenSymbol: Symbol of the input token, like ETH\n`
        }

        if (!params.outputTokenSymbol) {
            requiredFieldsText += `- outputTokenSymbol: Symbol of the output token, like USDT\n`
        }

        if (!params.inputToken) {
            requiredFieldsText += `- inputToken: Address of the input token\n`
        }

        if (!params.outputToken) {
            requiredFieldsText += `- outputToken: Address of the output token\n`
        }

        if (!params.amount) {
            requiredFieldsText += `- amount: Amount of input token to swap\n`
        }

        if (!params.chain) {
            requiredFieldsText += `- chain: Chain to swap on\n`
        }

        return requiredFieldsText;

    }

    async validateParams(params: SwapParams): Promise<string> {

        let notValidParamsText = ""; 

        const nativeToken = this.walletProvider.chains[params.chain].nativeCurrency.symbol;

        if (params.inputTokenSymbol.toLowerCase() === params.outputTokenSymbol.toLowerCase()) {
            notValidParamsText = 'Cannot Swap Same Token, symbols are same'
            return notValidParamsText;
        }

        if (params.inputTokenSymbol.toLowerCase() !== nativeToken.toLowerCase() && params.inputToken.toLowerCase() === zeroAddress) {
            notValidParamsText = `- inputToken: Address of the input token\n`
            return notValidParamsText;
        }

        if (params.outputTokenSymbol.toLowerCase() !== nativeToken.toLowerCase() && params.outputToken.toLowerCase() === zeroAddress) {
            notValidParamsText = `- outputToken: Address of the output token\n`
            return notValidParamsText;
        }

        return notValidParamsText;

    }
}

export const swapSingleAction = {
    name: "SWAP_SINGLE",
    description: "Use this action when user swap from evm chain to evm chain, from chain and to chain should not be solana or movement chain.",
    handler: async (
        runtime: IAgentRuntime,
        message: Memory,
        state: State,
        _options: Record<string, unknown>,
        callback?: HandlerCallback
    ) => {
        elizaLogger.info("Starting swap action...");

        if (message.content.source === "direct") {
            // continue
        } else {
            callback?.({
                text: "i can't do that for you.",
                content: { error: "Swap single not allowed" },
            });
            return false;
        }


        // Initialize or update state
        let currentState = state;
        if (!currentState) {
            currentState = (await runtime.composeState(message)) as State;
        } else {
            currentState = await runtime.updateRecentMessageState(currentState);
        }
        state.walletInfo = await evmWalletProvider.get(
            runtime,
            message,
            currentState
        );

        // Compose swap context
        const swapContext = composeContext({
            state: currentState,
            template: swapTemplate,
        });
        const content = await generateObjectDeprecated({
            runtime,
            context: swapContext,
            modelClass: ModelClass.LARGE,
        });

        elizaLogger.debug("Swap evm single token on same chain content:", content);

        const slippage = (!content?.slippage || content.slippage === null || content.slippage === "null") ? 0.5 : Number(content.slippage);

        const walletProvider = initWalletProvider(runtime);
        const action = new SwapAction(walletProvider);
        const swapParams: SwapParams = {
            inputTokenSymbol: content.inputTokenSymbol,
            outputTokenSymbol: content.outputTokenSymbol,
            inputToken: content.inputToken,
            outputToken: content.outputToken,
            amount: content.amount,
            chain: content.chain,
            slippage: slippage,
        };
        try {
            const swapResp = await action.swapSingle(swapParams);

            if (typeof swapResp === "string") {
                callback?.({
                    text: `You are trying to do token swap on same evm chain, but following fields are missing or not correct:\n${swapResp}`,
                    content: { error: swapResp }
                });
                return false;
            }

            callback?.({
                text: `Successfully swapped ${swapResp.fromAmount} ${swapResp.fromTokenSymbol} to ${swapResp.toAmount} ${swapResp.toTokenSymbol} to receipent address ${swapResp.recipient} on ${swapResp.toChain} chain\nTransaction Hash: ${swapResp.txHash}`,
                content: { ...swapResp },
            });

            return true;
        } catch (error) {
            elizaLogger.error("Error during single token swap:", error.message);
            callback?.({
                text: `Swap failed: ${error.message}`,
                content: { error: error.message }
            });
            return false;
        }
    },
    template: swapTemplate,

    validate: async (runtime: IAgentRuntime) => {
        const privateKey = runtime.getSetting("WALLET_PRIVATE_KEY");
        return typeof privateKey === "string" && privateKey.startsWith("0x");
    },
    examples: swapExamplesSingleToken,
    similes: ["SWAP", "TOKEN_SWAP", "SWAP_TOKEN", "SWAP_FUND", "CONVERT", "EXCHANGE_TOKEN", "TOKEN_CONVERSION"]

};


