   environment: { type: 'local' }
   setup:
     virtual_fs:
       "start.txt": "This file already exists."
   run:
     - input: "echo 'Agent response.' && echo 'extra line' > result.txt"
       evaluations:
         # Test Pass
         - type: "string_contains"
