// packages/cli/src/commands/scenario.ts
import type { Argv } from 'yargs';
import yaml from 'js-yaml';
import fs from 'fs';
import path from 'path';
import { elizaLogger } from '@elizaos/core'; // Assuming a shared logger
// Define the arguments for the 'run' subcommand
interface ScenarioRunArgs {
  filePath: string;
  live: boolean;
}
// Command definition for 'scenario run'
export const command = 'scenario <command>';
export const desc = 'Manage and execute ElizaOS scenarios.';
export const builder = (yargs: Argv): Argv => {
  return yargs.command(
    'run <filePath>',
    'Execute a scenario from a YAML file.',
    (yargs: Argv) => {
      return yargs
        .positional('filePath', {
          describe: 'Path to the .scenario.yaml file',
          type: 'string',
          demandOption: true,
        })
        .option('live', {
          alias: 'l',
          type: 'boolean',
          description: 'Run scenario in live mode, ignoring mocks.',
          default: false,
        });
    },
    (argv: ScenarioRunArgs) => {
      // This is the handler function that executes when the command is called
      handleRunScenario(argv);
    }
  );
};
// The core logic for the command
function handleRunScenario(args: ScenarioRunArgs) {
  elizaLogger.info(`Starting scenario run with args: ${JSON.stringify(args)}`);
  try {
    const fullPath = path.resolve(args.filePath);
    elizaLogger.info(`Attempting to read scenario file from: ${fullPath}`);
    if (!fs.existsSync(fullPath)) {
      elizaLogger.error(`Error: File not found at '${fullPath}'`);
      process.exit(1);
    }
    const fileContents = fs.readFileSync(fullPath, 'utf8');
    const scenario = yaml.load(fileContents);
    console.log('--- Parsed Scenario Content ---');
    console.log(JSON.stringify(scenario, null, 2));
    console.log('-----------------------------');

    elizaLogger.info('Scenario file parsed successfully.');
  } catch (error) {
    elizaLogger.error('An error occurred during scenario execution:', error);
    process.exit(1);
  }
}
